import React ,{useEffect , useState} from 'react';
import './App.css';
import { Table, Button, Dropdown } from "react-bootstrap";


function App() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);


  useEffect(() => {
    getDatas()
  }, [])

  const getDatas = async () => {
    // const res = await fetch('https://restcountries.eu/rest/v2/all')
    // const data = await res.json()
    // setItems(data)

    fetch("https://restcountries.eu/rest/v2/all")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },

        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }

  const searchData = async term => {
      if(term === '') return
      const res = await fetch(`https://restcountries.eu/rest/v2/name/${term}`)
      const data = await res.json()
      setItems(data)
  }

  const filterByRegion = async region => {
    if(region === 'all') {
      const res = await fetch('https://restcountries.eu/rest/v2/all')
      const data = await res.json()
      setItems(data)
    }else{
      const res = await fetch(`https://restcountries.eu/rest/v2/region/${region}`)
      const data = await res.json()
      setItems(data)
    } 

}
  const sortStraight = async () => {
    const res = await fetch('https://restcountries.eu/rest/v2/all')
    const data = await res.json()
    const sortedData = data.sort((a,b) => a.name > b.name ? 1 : -1)
    setItems(sortedData)
  }

  const sortReverse = async () => {
    const res = await fetch('https://restcountries.eu/rest/v2/all')
    const data = await res.json()
    const sortedData = data.sort((a,b) => a.name < b.name ? 1 : -1)
    setItems(sortedData)
  }

  const deleteCountryfromList = async name => {
    const newData = items.filter(item => item.name !== name);
    setItems(newData)
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <ul>
        {items.map(item => (
          <li key={item.id}>
            {item.name} {item.price}
          </li>
        ))}
      </ul>
    );
  }


  }


export default App;
