<table>
<tr>
  <th>Country</th>
  <th>Region</th>
  <th>NativeName</th>
</tr>

{items.map(item => (         
  <tr>
    <td> {item.name}</td>
    <td>Griffin</td>
    <td>$100</td>
  </tr>
         ))}


</table>
    );
  }
}

export default App;
