import React ,{useEffect , useState} from 'react';
import './App.css';



function App() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  useEffect(() => {
    getDatas()
  }, [])

  const getDatas = async () => {
    const res = await fetch('https://restcountries.eu/rest/v2/all')
    const data = await res.json()
    await setItems(data)
  }

  const searchData = async term => {
      if(term === '') return
      const res = await fetch(`https://restcountries.eu/rest/v2/name/${term}`)
      const data = await res.json()
      await setItems(data)
  }

  const filterByRegion = async region => {
    if(region === 'all') {
      const res = await fetch('https://restcountries.eu/rest/v2/all')
      const data = await res.json()
      await setItems(data)
    }else{
      const res = await fetch(`https://restcountries.eu/rest/v2/region/${region}`)
      const data = await res.json()
      await setItems(data)
    } 

}
  const sortStraight = async () => {
    const res = await fetch('https://restcountries.eu/rest/v2/all')
    const data = await res.json()
    const sortedData = data.sort((a,b) => a.name > b.name ? 1 : -1)
    await setItems(sortedData)
  }

  const sortReverse = async () => {
    const res = await fetch('https://restcountries.eu/rest/v2/all')
    const data = await res.json()
    const sortedData = data.sort((a,b) => a.name < b.name ? 1 : -1)
    await setItems(sortedData)
  }

  const DeleteItem = async () => {
    const res = await fetch(`https://restcountries.eu/rest/v2/all/${id}`)
    axios.delete(res).then(res => {
        const del = items.filter(item => id !== item.id)
        setItems(del)
        console.log('res', res)
    })
  }


  return (
  <div className="container">
    <div>
      <input type="text" placeholder="Search..."
          onChange={(term) => searchData(term.target.value)}
      />
      <button onClick={() => sortStraight()}>A-Z</button>
      <button onClick={() => sortReverse()}>Z-A</button>
      <select onChange={val => filterByRegion(val.target.value)}>
        <option value="all">Filter by Region</option>
        <option value="africa">Africa</option>
        <option value="americas">Americas</option>
        <option value="asia">Asia</option>
        <option value="europe">Europe</option>
        <option value="oceania">Oceania</option>
      </select>
    </div>
     <table className="table" id="customers">
       <thead>
          <tr>
                <th>Country</th>
                <th>Region</th>
                <th>NativeName</th>
                <th>flag</th>
                <th>Delete</th>
          </tr>

       </thead>
        <tbody>
            {items.map(item => (         
                <tr key={item.name}>
                    <td> {item.name}</td>
                    <td>{item.region}</td>
                    <td>{item.nativeName}</td>
                    <td><img style={{width: "30px"}} src={item.flag}/></td>
                    <td><button onClick={() => DeleteItem()}>Delete</button></td>
                </tr>
            ))}
        </tbody>

    </table>
  </div>

    );
  }


export default App;
